mapper.select:
SELECT * FROM orders
WHERE 1 = 1

     AND user_id = #{order.userId}


     AND demand_id = #{order.demandId}


     AND order_state = #{order.orderState}


     AND company_id = #{order.companyId}

 AND create_time BETWEEN #{order.fromTime} AND  #{order.toTime}
 ORDER BY last_update_time DESC

     limit #{index},#{size}

SELECT count(1)
FROM orders
WHERE 1 = 1

    AND user_id = #{userId}


    AND demand_id = #{demandId}


    AND order_state = #{orderState}


    AND company_id = #{companyId}

AND create_time BETWEEN #{fromTime} AND  #{toTime}

SELECT DISTINCT user_id from orders
WHERE company_id = #{companyId} AND order_state = #{orderState}
ORDER BY last_update_time DESC

SELECT *
FROM orders
WHERE company_id = #{companyId}
AND user_id = #{userId}
AND order_state = #{orderState}
ORDER BY last_update_time DESC
limit 0,1
